question_concerne.addEventListener('change', function() {
  if (question_concerne.value === 'logiciel') {
    console.log("ommmaaarrr"+question_concerne.value)
    logiciel.style.display = 'block';  // Make field2 visible
    label_logiciel.style.display = 'block';  // Make field2 visible
    logiciel.setAttribute('required', '');
    label_seul_impacte.style.display = 'block';  // Make field2 visible
    seul_impacte.style.display = 'block';  // Make field2 visible
    label_travaille.style.display = 'block';  // Make field2 visible
    travaille.style.display = 'block';  // Make field2 visible


    if (logiciel.value === 'sap') {
    label_num_transaction.style.display = 'block';
    num_transaction.style.display = 'block';
    num_transaction.setAttribute('required', '');



  } else {
    label_num_transaction.style.display = 'none';
    num_transaction.style.display = 'none';
    num_transaction.removeAttribute('required');
  }




  } else {
    label_logiciel.style.display = 'none';   // Hide field2
    logiciel.style.display = 'none';   // Hide field2
    logiciel.removeAttribute('required');
    label_seul_impacte.style.display = 'none';   // Hide field2
    seul_impacte.style.display = 'none';   // Hide field2
    label_travaille.style.display = 'none';   // Hide field2
    travaille.style.display = 'none';   // Hide field2

    label_num_transaction.style.display = 'none';
    num_transaction.style.display = 'none';
    num_transaction.removeAttribute('required');
  }



});

